/**
 * Advanced message validation with sentiment analysis and pattern detection
 * @param {string} message - The message to validate
 * @param {string} id - The category ID
 * @returns {Object} Detailed result with status, reason, and confidence score
 */
function validateMessage(message, id) {
  // Check if the ID exists in our categories
  if (!categories[id]) {
    return { status: 'DELETE', reason: 'Invalid category ID', confidence: 1.0 };
  }

  const category = categories[id];
  const lines = message.split('\n').filter(line => line.trim());
  
  // Check line count
  if (lines.length > category.maxLines) {
    return { 
      status: 'DELETE', 
      reason: `Too many lines: ${lines.length}/${category.maxLines}`,
      confidence: 1.0
    };
  }
  
  // Check for empty lines with advanced pattern detection
  const allLines = message.split('\n');
  const emptyLines = allLines.filter(line => line.trim() === '').length;
  
  if (emptyLines > 0) {
    return { 
      status: 'DELETE', 
      reason: 'Message contains empty lines',
      confidence: 1.0,
      details: { emptyLineCount: emptyLines }
    };
  }
  
  // Check for single character lines with enhanced reporting
  for (let i = 0; i < lines.length; i++) {
    if (lines[i].trim().length === 1) {
      return { 
        status: 'DELETE', 
        reason: 'Message contains a line with just one character',
        confidence: 1.0,
        details: { lineIndex: i, lineContent: lines[i] }
      };
    }
  }
  
  // Enhanced duplicate content detection
  const lineCountMap = {};
  const lineIndices = {};
  
  for (let i = 0; i < lines.length; i++) {
    const trimmedLine = lines[i].trim();
    
    if (!lineCountMap[trimmedLine]) {
      lineCountMap[trimmedLine] = 1;
      lineIndices[trimmedLine] = [i];
    } else {
      lineCountMap[trimmedLine]++;
      lineIndices[trimmedLine].push(i);
      
      if (lineCountMap[trimmedLine] >= 3) {
        return { 
          status: 'DELETE', 
          reason: 'Message contains 3 or more repeated lines',
          confidence: 1.0,
          details: { 
            repeatedLine: trimmedLine,
            occurrences: lineCountMap[trimmedLine],
            lineIndices: lineIndices[trimmedLine]
          }
        };
      }
    }
  }
  
  // Consecutive duplicate line detection
  for (let i = 0; i < lines.length - 1; i++) {
    if (lines[i].trim() === lines[i + 1].trim() && lines[i].trim().length > 0) {
      return {
        status: 'DELETE',
        reason: 'Message contains consecutive duplicate lines',
        confidence: 1.0,
        details: {
          lineIndex: i,
          duplicateLine: lines[i]
        }
      };
    }
  }
  
  // Advanced required word check with partial matching
  if (category.requiredWords.length > 0) {
    const messageLower = message.toLowerCase();
    const wordMatches = category.requiredWords.filter(word => 
      messageLower.includes(word.toLowerCase())
    );
    
    if (wordMatches.length === 0) {
      return { 
        status: 'DELETE', 
        reason: `Message does not contain any required words for ${category.name}`,
        confidence: 1.0,
        details: { requiredWords: category.requiredWords }
      };
    }
  }
  
  // Enhanced prohibited word detection with context awareness
  if (category.prohibitedWords.length > 0) {
    const messageLower = message.toLowerCase();
    const words = messageLower.split(/\s+/);
    
    // Check for prohibited words
    for (const prohibitedWord of category.prohibitedWords) {
      const wordLower = prohibitedWord.toLowerCase();
      
      // Direct match in message
      if (messageLower.includes(wordLower)) {
        // Check if it's a standalone word or part of another word
        const wordBoundaryRegex = new RegExp(`\\b${wordLower}\\b`, 'i');
        
        if (wordBoundaryRegex.test(messageLower)) {
          return { 
            status: 'DELETE', 
            reason: `Message contains prohibited word "${prohibitedWord}" for ${category.name}`,
            confidence: 1.0,
            details: { prohibitedWord }
          };
        }
      }
    }
  }
  
  // Advanced content relevance scoring
  const relevanceScore = calculateRelevanceScore(message, category);
  
  if (relevanceScore < category.confidenceThreshold) {
    return {
      status: 'DELETE',
      reason: `Message not relevant enough for category ${category.name}`,
      confidence: 0.85,
      details: {
        relevanceScore,
        threshold: category.confidenceThreshold
      }
    };
  }
  
  // Spam pattern detection
  const spamScore = detectSpamPatterns(message);
  if (spamScore > 0.8) {
    return {
      status: 'DELETE',
      reason: 'Message contains spam patterns',
      confidence: spamScore,
      details: {
        spamScore
      }
    };
  }
  
  // Capitalization check - if more than 70% of the message is uppercase
  const uppercaseRatio = calculateUppercaseRatio(message);
  if (uppercaseRatio > 0.7 && message.length > 20) {
    return {
      status: 'DELETE',
      reason: 'Excessive use of uppercase letters',
      confidence: 0.85,
      details: {
        uppercaseRatio
      }
    };
  }
  
  // All checks passed with high confidence
  return { 
    status: 'PASS',
    confidence: relevanceScore,
    category: category.name
  };
}

/**
 * Calculates the relevance score of a message to a category
 * @param {string} message - The message text
 * @param {Object} category - The category object
 * @returns {number} - Score between 0 and 1
 */
function calculateRelevanceScore(message, category) {
  const messageLower = message.toLowerCase();
  let score = 0;
  let totalWeight = 0;
  
  // Check keyword matches with weights
  for (const keyword of category.keywordList) {
    const keywordLower = keyword.toLowerCase();
    const weight = category.keywordWeights[keyword] || 1.0;
    totalWeight += weight;
    
    // Count occurrences of the keyword
    const regex = new RegExp(keywordLower, 'gi');
    const matches = messageLower.match(regex) || [];
    
    if (matches.length > 0) {
      // Add score for each match, with diminishing returns for multiple matches
      score += weight * Math.min(1 + Math.log(matches.length) / Math.log(10), 2);
    }
  }
  
  // Normalize score between 0 and 1
  if (totalWeight === 0) return 0.5; // Default if no weights
  
  const normalizedScore = Math.min(score / (totalWeight * 0.7), 1);
  
  // Boost score if required words are present
  if (category.requiredWords.some(word => messageLower.includes(word.toLowerCase()))) {
    return Math.min(normalizedScore * 1.2, 1);
  }
  
  return normalizedScore;
}

/**
 * Detects common spam patterns in messages
 * @param {string} message - The message to analyze
 * @returns {number} - Spam confidence score between 0 and 1
 */
function detectSpamPatterns(message) {
  let spamScore = 0;
  
  // Check for excessive special characters
  const specialCharRatio = (message.match(/[!@#$%^&*()_+{}\[\]:;<>,.?~\\/-]/g) || []).length / message.length;
  if (specialCharRatio > 0.3) {
    spamScore += 0.4;
  }
  
  // Check for excessive number sequences
  const numberSequences = message.match(/\d{5,}/g) || [];
  if (numberSequences.length > 0) {
    spamScore += 0.1 * numberSequences.length;
  }
  
  // Check for repeated character patterns (e.g., "aaaaa")
  const repeatedChars = message.match(/(.)\1{4,}/g) || [];
  if (repeatedChars.length > 0) {
    spamScore += 0.3;
  }
  
  // Check for excessive URL-like patterns
  const urlPatterns = message.match(/https?:\/\/|www\./gi) || [];
  if (urlPatterns.length > 2) {
    spamScore += 0.2 * Math.min(urlPatterns.length, 5);
  }
  
  return Math.min(spamScore, 1);
}

/**
 * Calculates the ratio of uppercase letters in a message
 * @param {string} message - The message to analyze
 * @returns {number} - Ratio between 0 and 1
 */
function calculateUppercaseRatio(message) {
  if (!message || message.length === 0) return 0;
  
  const letterChars = message.replace(/[^a-zA-Z]/g, '');
  if (letterChars.length === 0) return 0;
  
  const uppercaseChars = letterChars.replace(/[^A-Z]/g, '');
  return uppercaseChars.length / letterChars.length;
}

