app.post('/api/keywords', (req, res) => {
  try {
    const { id, keyword, action } = req.body;
    
    if (!id || !keyword || !action) {
      return res.status(400).json({ 
        status: 'ERROR', 
        reason: 'Missing required parameters: id, keyword, and action' 
      });
    }
    
    if (!categories[id]) {
      return res.status(400).json({ 
        status: 'ERROR', 
        reason: 'Invalid category ID' 
      });
    }
    
    if (action === 'add') {
      // Check if keyword already exists
      if (categories[id].keywordList.includes(keyword)) {
        return res.status(400).json({
          status: 'ERROR',
          reason: 'Keyword already exists in this category'
        });
      }
      
      // Add keyword with default weight
      categories[id].keywordList.push(keyword);
      categories[id].keywordWeights[keyword] = 1.0;
      
      res.json({ 
        status: 'SUCCESS', 
        message: 'Keyword added',
        details: {
          categoryName: categories[id].name,
          keywordCount: categories[id].keywordList.length
        }
      });
    } else if (action === 'remove') {
      const index = categories[id].keywordList.indexOf(keyword);
      if (index > -1) {
        // Remove from list and weights
        categories[id].keywordList.splice(index, 1);
        delete categories[id].keywordWeights[keyword];
        
        res.json({ 
          status: 'SUCCESS', 
          message: 'Keyword removed',
          details: {
            categoryName: categories[id].name,
            keywordCount: categories[id].keywordList.length
          }
        });
      } else {
        res.status(400).json({ 
          status: 'ERROR', 
          reason: 'Keyword not found in category' 
        });
      }
    } else if (action === 'update_weight') {
      const weight = parseFloat(req.body.weight);
      
      if (isNaN(weight) || weight <= 0) {
        return res.status(400).json({
          status: 'ERROR',
          reason: 'Invalid weight value. Must be a positive number'
        });
      }
      
      if (!categories[id].keywordList.includes(keyword)) {
        return res.status(400).json({
          status: 'ERROR',
          reason: 'Keyword not found in category'
        });
      }
      
      categories[id].keywordWeights[keyword] = weight;
      
      res.json({
        status: 'SUCCESS',
        message: 'Keyword weight updated',
        details: {
          keyword,
          weight
        }
      });
    } else {
      res.status(400).json({ 
        status: 'ERROR', 
        reason: 'Invalid action. Use "add", "remove", or "update_weight"' 
      });
    }
  } catch (error) {
    res.status(500).json({ 
      status: 'ERROR', 
      reason: 'Server error: ' + error.message 
    });
  }
});

