// Function to evaluate moderation results and make a decision
function evaluateModeration(moderationData) {
  // Set thresholds for various content types
  const thresholds = {
    nudity: {
      sexual_activity: 0.4,
      sexual_display: 0.4,
      erotica: 0.4,
      very_suggestive: 0.5,
      suggestive: 0.7
    },
    weapon: 0.7,
    alcohol: 0.8,
    recreational_drug: 0.6,
    offensive: 0.5,
    scam: 0.7,
    gore: 0.5,
    violence: 0.6,
    gambling: 0.7,
    "self-harm": 0.5
  };

  // Check nudity categories
  if (moderationData.nudity) {
    for (const category in thresholds.nudity) {
      if (moderationData.nudity[category] >= thresholds.nudity[category]) {
        return {
          action: "DELETE",
          reason: `Inappropriate content detected: Nudity (${category})`
        };
      }
    }
  }

  // Check other categories with simple thresholds
  const simpleCategories = ["weapon", "alcohol", "recreational_drug", "scam", "gore", "violence", "gambling", "self-harm"];
  for (const category of simpleCategories) {
    if (moderationData[category] && moderationData[category].prob >= thresholds[category]) {
      return {
        action: "DELETE",
        reason: `Inappropriate content detected: ${category}`
      };
    }
  }

  // Check offensive content
  if (moderationData.offensive) {
    const offensiveCategories = ["nazi", "asian_swastika", "confederate", "supremacist", "terrorist", "middle_finger"];
    for (const category of offensiveCategories) {
      if (moderationData.offensive[category] >= thresholds.offensive) {
        return {
          action: "DELETE",
          reason: `Offensive content detected: ${category}`
        };
      }
    }
  }

  // If no issues found, allow the content
  return {
    action: "SUCCESS",
    reason: "Content meets community guidelines"
  };
}

