export async function performAdvancedSearch(options) {
  const {
    query,
    type = 'web',
    exactTerms = [],
    excludeTerms = [],
    siteSearch = '',
    fileType = '',
    timeRange = '',
    country = 'us',
    lang = 'en'
  } = options;

  
  let advancedQuery = query || '';

 
  if (siteSearch) {
    advancedQuery += ' site:' + siteSearch;
  }

 
  if (fileType) {
    advancedQuery += ' filetype:' + fileType;
  }

  return performGoogleSearch(advancedQuery, {
    type,
    country,
    lang,
    exactTerms,
    excludeTerms,
    timeRange
  });
}


const searchCache = {
  cache: new Map(),
  ttl: 60 * 60 * 1000, // 1 hour

 
  get(key) {
    if (this.cache.has(key)) {
      const { results, timestamp } = this.cache.get(key);
      const now = Date.now();

      
      if (now - timestamp < this.ttl) {
        console.log('Cache hit for "' + key + '"');
        return results;
      } else {
        console.log('Cache expired for "' + key + '"');
        this.cache.delete(key);
      }
    }

    return null;
  },

  
  set(key, results) {
    this.cache.set(key, {
      results,
      timestamp: Date.now()
    });
    console.log('Cached results for "' + key + '"');
  },

  
  generateKey(options) {
    return JSON.stringify(options);
  }
};


export async function performCachedSearch(options) {
  const cacheKey = searchCache.generateKey(options);
  const cachedResults = searchCache.get(cacheKey);

  if (cachedResults) {
    return cachedResults;
  }

  const results = await performAdvancedSearch(options);
  searchCache.set(cacheKey, results);
  return results;
}

