function generateWebResultsHTML(results) {
  return results.map(result =>
    '<div class="result"><div class="result-url"><cite>' + (result.siteName || result.link) + '</cite></div><a href="' + result.link + '" class="result-title">' + result.title + '</a><div class="result-snippet">' + result.snippet + '</div><div class="result-meta">' + (result.date ? '<span>' + result.date + '</span>' : '') + '</div></div>'
  ).join('');
}


function generateImageResultsHTML(results) {
  return (
    '<div class="image-results">' +
    results.map(result =>
      '<div class="image-result"><a href="' + result.link + '" target="_blank"><img src="' + result.imgSrc + '" alt="' + result.title + '"></a><div class="image-result-info"><div class="image-result-title">' + result.title + '</div></div></div>'
    ).join('') +
    '</div>'
  );
}


function generateNewsResultsHTML(results) {
  return results.map(result =>
    '<div class="news-result"><div class="news-source"><span>' + result.source + '</span><span class="news-time">' + result.time + '</span></div><a href="' + result.link + '" class="result-title">' + result.title + '</a><div class="result-snippet">' + result.snippet + '</div></div>'
  ).join('');
}


function generateVideoResultsHTML(results) {
  return results.map(result =>
    '<div class="video-result"><div class="video-thumbnail"><img src="' + result.imgSrc + '" alt="' + result.title + '"><span class="video-duration">' + result.duration + '</span></div><div><a href="' + result.link + '" class="result-title">' + result.title + '</a><div class="video-source"><span>' + result.source + '</span></div></div></div>'
  ).join('');
}







