app.post('/api/verify-captcha', async (req, res) => {
  const { recaptchaV2Token, termsAccepted, privacyAccepted, eulaAccepted } = req.body;

  if (!recaptchaV2Token) {
    return res.status(400).json({
      success: false,
      message: 'reCAPTCHA v2 token is required.'
    });
  }

  if (!termsAccepted || !privacyAccepted || !eulaAccepted) {
    return res.status(400).json({
      success: false,
      message: 'You must accept the Terms of Service, Privacy Policy, and EULA.'
    });
  }

  try {
    // Verify the reCAPTCHA v2 token.
    const v2Data = await fetch('https://www.google.com/recaptcha/api/siteverify', {
      method: 'POST',
      headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
      body: 'secret=' + encodeURIComponent(process.env.RECAPTCHA_SECRET_KEY) +
            '&response=' + encodeURIComponent(recaptchaV2Token)
    }).then(r => r.json());

    if (!v2Data.success) {
      return res.status(400).json({
        success: false,
        message: 'reCAPTCHA v2 verification failed. Please try again.'
      });
    }

    // Both tokens verified successfully—generate the OAuth URL and state.
    const { state, url: redirectUrl } = discord.getOAuthUrl();

    return res.json({
      success: true,
      redirectUrl
    });
  } catch (err) {
    console.error('Error verifying CAPTCHA:', err);
    return res.status(500).json({
      success: false,
      message: 'An error occurred while verifying CAPTCHA. Please try again.'
    });
  }
});



