const header = document.getElementById('carousel-header');
let selectedLanguage = "United States"; // default

// Function to update the selected language
function updateSelectedLanguage(item) {
  if (!item) return;

  const prevSelected = carousel.querySelector('.carousel-item.selected');
  if (prevSelected) {
    prevSelected.classList.remove('selected');
    prevSelected.querySelector('input[type="radio"]').checked = false;
  }

  item.classList.add('selected');
  item.querySelector('input[type="radio"]').checked = true;
  selectedLanguage = item.dataset.language;
  header.textContent = languageStrings[selectedLanguage] || "Is this the right country or region?";
}

// Event listener for carousel item click
carousel.addEventListener('click', function(event) {
  const item = event.target.closest('.carousel-item');
  updateSelectedLanguage(item);
});

// 4) Next button functionality
