// Add parallax effect to animation container
document.addEventListener('mousemove', function(e) {
  const container = document.querySelector('.animation-container');
  const xAxis = (window.innerWidth / 2 - e.pageX) / 30;
  const yAxis = (window.innerHeight / 2 - e.pageY) / 30;
  container.style.transform = "translate(" + xAxis + "px, " + yAxis + "px)";
});

// Add keyboard navigation
document.addEventListener('keydown', function(e) {
  const items = Array.from(document.querySelectorAll('.carousel-item'));
  const currentIndex = items.findIndex(function(item) {
    return item.classList.contains('selected');
  });

  if (e.key === 'ArrowDown') {
    const nextIndex = (currentIndex + 1) % items.length;
    updateSelectedLanguage(items[nextIndex]);
  }

  if (e.key === 'ArrowUp') {
    const prevIndex = (currentIndex - 1 + items.length) % items.length;
    updateSelectedLanguage(items[prevIndex]);
  }
});

document.addEventListener('keydown', function(event) {
    // Get the pressed key, convert to uppercase
    const pressedKey = event.key.toUpperCase();

    // Find the first carousel item that starts with the pressed key
    const carouselItems = document.querySelectorAll('.carousel-item');
    let closestItem = null;

    carouselItems.forEach(item => {
      const label = item.querySelector('label').textContent.trim();
      if (label.startsWith(pressedKey)) {
        closestItem = item;
        return; // Exit the loop once the first match is found
      }
    });

    // If a matching item is found, scroll to it
    if (closestItem) {
      closestItem.scrollIntoView({ behavior: 'smooth', block: 'center' });
    }
  });


