            // Password visibility toggle
            const togglePassword = document.getElementById('togglePassword');
            const passwordInput = document.getElementById('password');
            
            togglePassword.addEventListener('click', function() {
                const type = passwordInput.getAttribute('type') === 'password' ? 'text' : 'password';
                passwordInput.setAttribute('type', type);
                this.querySelector('i').classList.toggle('ti-eye');
                this.querySelector('i').classList.toggle('ti-eye-off');
            });
            
            // Password strength checker
            const passwordStrengthBar = document.getElementById('password-strength');
            const passwordStrengthText = document.getElementById('password-strength-text');
            const reqLength = document.getElementById('req-length');
            const reqUppercase = document.getElementById('req-uppercase');
            const reqLowercase = document.getElementById('req-lowercase');
            const reqNumber = document.getElementById('req-number');
            const reqSpecial = document.getElementById('req-special');
            
            passwordInput.addEventListener('input', function() {
                const password = this.value;
                let strength = 0;
                
                // Check requirements
                const hasLength = password.length >= 8;
                const hasUpperCase = /[A-Z]/.test(password);
                const hasLowerCase = /[a-z]/.test(password);
                const hasNumbers = /\d/.test(password);
                const hasSpecial = /[!@#$%^&*(),.?":{}|<>]/.test(password);
                
                // Update requirement indicators
                updateRequirement(reqLength, hasLength);
                updateRequirement(reqUppercase, hasUpperCase);
                updateRequirement(reqLowercase, hasLowerCase);
                updateRequirement(reqNumber, hasNumbers);
                updateRequirement(reqSpecial, hasSpecial);
                
                // Calculate strength
                if (hasLength) strength += 20;
                if (hasUpperCase) strength += 20;
                if (hasLowerCase) strength += 20;
                if (hasNumbers) strength += 20;
                if (hasSpecial) strength += 20;
                
                // Update strength bar
                passwordStrengthBar.style.width = strength + '%';
                
                // Update strength class and text
                if (strength < 40) {
                    passwordStrengthBar.className = 'progress-bar progress-bar-weak';
                    passwordStrengthText.textContent = 'Weak password';
                    passwordStrengthText.className = 'text-danger small mt-1';
                } else if (strength < 80) {
                    passwordStrengthBar.className = 'progress-bar progress-bar-medium';
                    passwordStrengthText.textContent = 'Medium password';
                    passwordStrengthText.className = 'text-warning small mt-1';
                } else {
                    passwordStrengthBar.className = 'progress-bar progress-bar-strong';
                    passwordStrengthText.textContent = 'Strong password';
                    passwordStrengthText.className = 'text-success small mt-1';
                }
            });
            
            function updateRequirement(element, isMet) {
                if (isMet) {
                    element.classList.add('met');
                    element.classList.remove('unmet');
                    element.querySelector('i').className = 'ti ti-circle-check';
                } else {
                    element.classList.add('unmet');
                    element.classList.remove('met');
                    element.querySelector('i').className = 'ti ti-circle';
                }
            }
            
            // Multi-step form navigation
            const formSteps = document.querySelectorAll('.form-step');
            const nextButtons = document.querySelectorAll('.next-step');
            const prevButtons = document.querySelectorAll('.prev-step');
            const stepIndicators = document.querySelectorAll('.step');
            const progressBar = document.getElementById('progress-bar');
            let currentStep = 0;
            
            // Update progress bar
            function updateProgressBar() {
                const stepWidth = 100 / (stepIndicators.length - 1);
                progressBar.style.width = (currentStep * stepWidth) + '%';
            }
            
            // Show step
            function showStep(stepIndex) {
                formSteps.forEach((step, index) => {
                    step.classList.toggle('active', index === stepIndex);
                });
                
                stepIndicators.forEach((indicator, index) => {
                    indicator.classList.toggle('active', index === stepIndex);
                    indicator.classList.toggle('completed', index < stepIndex);
                });
                
                currentStep = stepIndex;
                updateProgressBar();
            }
            
            // Next step button
            nextButtons.forEach(button => {
                button.addEventListener('click', function() {
                    if (currentStep < formSteps.length - 1) {
                        showStep(currentStep + 1);
                    }
                });
            });
            
            // Previous step button
            prevButtons.forEach(button => {
                button.addEventListener('click', function() {
                    if (currentStep > 0) {
                        showStep(currentStep - 1);
                    }
                });
            });
            
            // Glow effect on mouse move
            const glow = document.getElementById('glow');
            document.addEventListener('mousemove', function(e) {
                const x = e.clientX;
                const y = e.clientY;
                
                glow.style.opacity = '1';
                glow.style.top = (y - 100) + 'px';
                glow.style.left = (x - 100) + 'px';
            });
            
            // Form submission (just for demo, would need backend integration)
            const form = document.getElementById('signup-form');
            form.addEventListener('submit', function(e) {
                e.preventDefault();
                // In a real application, this would send data to a server
                console.log('Form submitted');
            });
