function drawWatermark(ctx, width, height) {
  ctx.save();
  ctx.globalAlpha = 0.15;
  ctx.strokeStyle = "#ccc";
  ctx.lineWidth = 1;

  // Create multiple wave patterns with varying amplitudes and frequencies
  const waveCount = 5;
  for (let j = 0; j < waveCount; j++) {
    const amplitude = 3 + Math.random() * 7;
    const frequency = (1.5 + Math.random()) * Math.PI / width;
    const phase = Math.random() * 2 * Math.PI;
    const offsetY = height * (j + 1) / (waveCount + 1);

    ctx.beginPath();
    ctx.moveTo(0, offsetY + Math.sin(phase) * amplitude);

    // Use Bezier curves for smoother, more natural waves
    for (let x = 0; x <= width; x += width / 50) {
      const nextX = x + width / 50;
      const y1 = offsetY + Math.sin(frequency * x + phase) * amplitude;
      const y2 = offsetY + Math.sin(frequency * nextX + phase) * amplitude;
      const cpX = (x + nextX) / 2;
      const cpY = offsetY + Math.sin(frequency * cpX + phase) * amplitude * 1.2;

      if (x === 0) ctx.moveTo(x, y1);
      ctx.quadraticCurveTo(cpX, cpY, Math.min(nextX, width), y2);
    }
    ctx.stroke();
  }
  ctx.restore();
}

function drawExtraWatermark(ctx, width, height) {
  ctx.save();

  // Create diagonal pattern for better visual effect
  ctx.globalAlpha = 0.08;
  ctx.font = "bold 12px 'Courier New', monospace";

  // Gradient text for more sophisticated look
  const gradient = ctx.createLinearGradient(0, 0, width, height);
  gradient.addColorStop(0, "#8b0000");
  gradient.addColorStop(0.5, "#ff0000");
  gradient.addColorStop(1, "#8b0000");
  ctx.fillStyle = gradient;

  // Rotate the entire context for diagonal watermark
  ctx.translate(width / 2, height / 2);
  ctx.rotate(-Math.PI / 6);
  ctx.translate(-width / 2, -height / 2);

  for (let y = -height; y < height * 2; y += 30) {
    for (let x = -width / 2; x < width * 1.5; x += 100) {
      ctx.fillText("SECURE", x, y);
    }
  }
  ctx.restore();
}

function drawWaveDistortion(ctx, width, height) {
  ctx.save();
  ctx.globalAlpha = 0.3;

  // Create multiple waves with varying properties
  const waveCount = 3;
  for (let w = 0; w < waveCount; w++) {
    const amplitude = 5 + Math.random() * 7;
    const frequency = (0.5 + Math.random() * 2) * Math.PI / width;
    const offset = Math.random() * 2 * Math.PI;

    // Use different colors for each wave
    const hue = Math.floor(Math.random() * 360);
    ctx.strokeStyle = "hsla(" + hue + ", 70%, 50%, 0.2)";
    ctx.lineWidth = 0.8 + Math.random() * 1.2;

    // Create wavy paths with Bezier curves
    ctx.beginPath();
    let lastY = height * (w + 1) / (waveCount + 1) + Math.sin(offset) * amplitude;
    ctx.moveTo(0, lastY);

    for (let x = 0; x <= width; x += width / 40) {
      // Add some noise to make waves more natural
      const noise = (Math.random() - 0.5) * amplitude * 0.3;
      const y = height * (w + 1) / (waveCount + 1) +
                Math.sin(x * frequency + offset) * amplitude +
                noise;

      // Use bezier curves for smoother appearance
      const cpX = x - width / 80;
      const cpY = (lastY + y) / 2 + (Math.random() - 0.5) * amplitude * 0.2;
      ctx.quadraticCurveTo(cpX, cpY, x, y);
      lastY = y;
    }
    ctx.stroke();
  }
  ctx.restore();
}

function drawCircleDistortion(ctx, width, height) {
  ctx.save();
  ctx.globalAlpha = 0.25;

  // Create concentric circles with varying properties
  const numCircles = 15 + Math.floor(Math.random() * 10);
  const centerX = width * (0.4 + Math.random() * 0.2);
  const centerY = height * (0.4 + Math.random() * 0.2);

  for (let i = 0; i < numCircles; i++) {
    // Vary the circle properties
    const radius = 5 + Math.pow(Math.random(), 0.5) * Math.min(width, height) * 0.4;
    const concentricOffset = (Math.random() - 0.5) * 15;
    const x = centerX + concentricOffset;
    const y = centerY + concentricOffset;

    // Use gradient strokes for more appealing visuals
    const hue = (i * 20) % 360;
    ctx.strokeStyle = "hsla(" + hue + ", 60%, 50%, " + (0.1 + Math.random() * 0.2) + ")";
    ctx.lineWidth = 0.5 + Math.random() * 1.5;

    // Add slight distortion to circles
    ctx.beginPath();
    for (let angle = 0; angle <= Math.PI * 2; angle += Math.PI / 20) {
      const distortionAmount = 0.85 + Math.random() * 0.3;
      const rx = radius * distortionAmount;
      const ry = radius / distortionAmount;
      const px = x + Math.cos(angle) * rx;
      const py = y + Math.sin(angle) * ry;

      if (angle === 0) {
        ctx.moveTo(px, py);
      } else {
        ctx.lineTo(px, py);
      }
    }
    ctx.closePath();
    ctx.stroke();
  }
  ctx.restore();
}

function drawColoredLinesDistortion(ctx, width, height) {
  ctx.save();
  ctx.globalAlpha = 0.25;
  ctx.lineWidth = 0.8;

  // Create more structured pattern of lines
  const numLines = 15 + Math.floor(Math.random() * 15);

  for (let i = 0; i < numLines; i++) {
    // Create gradient lines with bezier curves
    const gradient = ctx.createLinearGradient(0, 0, width, height);
    gradient.addColorStop(0, "hsla(" + Math.random() * 360 + ", 70%, 50%, 0.4)");
    gradient.addColorStop(1, "hsla(" + Math.random() * 360 + ", 70%, 50%, 0.4)");
    ctx.strokeStyle = gradient;

    // Create curves instead of straight lines
    ctx.beginPath();
    const startX = Math.random() * width;
    const startY = Math.random() * height;
    const endX = Math.random() * width;
    const endY = Math.random() * height;

    // Control points for bezier curve
    const cp1x = (startX + endX) / 2 + (Math.random() - 0.5) * width * 0.5;
    const cp1y = (startY + endY) / 2 + (Math.random() - 0.5) * height * 0.5;
    const cp2x = (startX + endX) / 2 + (Math.random() - 0.5) * width * 0.5;
    const cp2y = (startY + endY) / 2 + (Math.random() - 0.5) * height * 0.5;

    ctx.moveTo(startX, startY);
    ctx.bezierCurveTo(cp1x, cp1y, cp2x, cp2y, endX, endY);
    ctx.lineWidth = 0.5 + Math.random() * 2;
    ctx.stroke();
  }
  ctx.restore();
}

function drawRandomWords(ctx, width, height) {
  ctx.save();
  ctx.globalAlpha = 0.5;
  ctx.font = "bold 14px Arial";
  ctx.fillStyle = "rgba(0, 0, 0, 0.5)";

  const numWords = 5 + Math.floor(Math.random() * 5);
  for (let i = 0; i < numWords; i++) {
    const word = randomWords[Math.floor(Math.random() * randomWords.length)];
    const x = Math.random() * width;
    const y = Math.random() * height;
    ctx.fillText(word, x, y);
  }
  ctx.restore();
}

function drawTile(canvas, emoji, isSelected, rotation, backgroundColor) {
  const ctx = canvas.getContext("2d");
  ctx.clearRect(0, 0, canvas.width, canvas.height);

  // Create gradient background instead of solid color
  const gradient = ctx.createRadialGradient(
    canvas.width / 2, canvas.height / 2, 0,
    canvas.width / 2, canvas.height / 2, canvas.width / 1.5
  );
  gradient.addColorStop(0, backgroundColor);
  gradient.addColorStop(1, adjustColor(backgroundColor, -30));
  ctx.fillStyle = gradient;
  ctx.fillRect(0, 0, canvas.width, canvas.height);

  // Add texture to background
  addNoise(ctx, canvas.width, canvas.height, 0.03);

  // Draw emoji with shadow and glow effects
  ctx.save();
  ctx.translate(canvas.width / 2, canvas.height / 2);
  ctx.rotate(rotation * Math.PI / 2); // Use full radians for more precise rotation


  // Emoji with gradient
  const fontSize = canvas.width * 0.4;
  ctx.font = fontSize + "px Arial";
  ctx.textAlign = "center";
  ctx.textBaseline = "middle";


  // Draw main emoji
  ctx.globalAlpha = 1.0;
  ctx.shadowBlur = 0;
  ctx.fillStyle = "#000";
  ctx.fillText(emoji, 0, 0);
  ctx.restore();

  // Apply distortion effects
  for (let i = 0; i < 25; i++) {
    const hue = Math.floor(Math.random() * 360);
    ctx.strokeStyle = "hsla(" + hue + ", 70%, 50%, 0.2)";
    ctx.lineWidth = Math.random() * 1.5 + 0.5;

    // Create curved lines instead of straight ones
    ctx.beginPath();
    const startX = Math.random() * canvas.width;
    const startY = Math.random() * canvas.height;
    const endX = Math.random() * canvas.width;
    const endY = Math.random() * canvas.height;

    const cpX = (startX + endX) / 2 + (Math.random() - 0.5) * canvas.width * 0.3;
    const cpY = (startY + endY) / 2 + (Math.random() - 0.5) * canvas.height * 0.3;

    ctx.moveTo(startX, startY);
    ctx.quadraticCurveTo(cpX, cpY, endX, endY);
    ctx.stroke();
  }

  // Add decorative particles
  for (let i = 0; i < 20; i++) {
    const hue = Math.floor(Math.random() * 360);
    ctx.fillStyle = "hsla(" + hue + ", 70%, 60%, 0.3)";
    let x = Math.random() * canvas.width;
    let y = Math.random() * canvas.height;
    let radius = Math.random() * 3 + 1;

    // Create glowing particles
    const glow = ctx.createRadialGradient(x, y, 0, x, y, radius * 2);
    glow.addColorStop(0, "hsla(" + hue + ", 70%, 60%, 0.6)");
    glow.addColorStop(1, "hsla(" + hue + ", 70%, 60%, 0)");

    ctx.fillStyle = glow;
    ctx.beginPath();
    ctx.arc(x, y, radius * 2, 0, Math.PI * 2);
    ctx.fill();

    // Add particle center
    ctx.fillStyle = "hsla(" + hue + ", 70%, 90%, 0.8)";
    ctx.beginPath();
    ctx.arc(x, y, radius * 0.5, 0, Math.PI * 2);
    ctx.fill();
  }

  // Add decorative circles with gradients
  for (let i = 0; i < 15; i++) {
    const hue = Math.floor(Math.random() * 360);
    let x = Math.random() * canvas.width;
    let y = Math.random() * canvas.height;
    let radius = Math.random() * 15 + 5;

    // Create gradient stroke
    const gradient = ctx.createRadialGradient(x, y, 0, x, y, radius);
    gradient.addColorStop(0, "hsla(" + hue + ", 70%, 50%, 0.1)");
    gradient.addColorStop(1, "hsla(" + hue + ", 70%, 50%, 0.4)");

    ctx.strokeStyle = gradient;
    ctx.lineWidth = Math.random() + 0.5;
    ctx.beginPath();

    // Create slightly distorted circles
    for (let angle = 0; angle <= Math.PI * 2; angle += Math.PI / 15) {
      const distortion = 0.85 + Math.random() * 0.3;
      const rx = radius * distortion;
      const ry = radius / distortion;
      const px = x + Math.cos(angle) * rx;
      const py = y + Math.sin(angle) * ry;

      if (angle === 0) {
        ctx.moveTo(px, py);
      } else {
        ctx.lineTo(px, py);
      }
    }
    ctx.closePath();
    ctx.stroke();
  }

  // Draw random words
  drawRandomWords(ctx, canvas.width, canvas.height);

  // Apply multiple distortion layers
  drawWatermark(ctx, canvas.width, canvas.height);
  drawWaveDistortion(ctx, canvas.width, canvas.height);
  drawCircleDistortion(ctx, canvas.width, canvas.height);
  drawColoredLinesDistortion(ctx, canvas.width, canvas.height);

  // Add subtle vignette effect
  addVignette(ctx, canvas.width, canvas.height);

  

  // Add selection indicator
  if (isSelected) {
    const ballRadius = 12;
    const ballX = canvas.width - ballRadius - 5;
    const ballY = ballRadius + 5;

    // Draw glowing indicator
    const glow = ctx.createRadialGradient(ballX, ballY, 0, ballX, ballY, ballRadius * 1.5);
    glow.addColorStop(0, "rgba(0, 131, 143, 0.8)");
    glow.addColorStop(1, "rgba(0, 131, 143, 0)");

    ctx.fillStyle = glow;
    ctx.beginPath();
    ctx.arc(ballX, ballY, ballRadius * 1.5, 0, 2 * Math.PI);
    ctx.fill();

    // Draw main indicator
    const gradient = ctx.createRadialGradient(ballX, ballY, 0, ballX, ballY, ballRadius);
    gradient.addColorStop(0, "#26c6da");
    gradient.addColorStop(1, "#00838f");

    ctx.fillStyle = gradient;
    ctx.beginPath();
    ctx.arc(ballX, ballY, ballRadius, 0, 2 * Math.PI);
    ctx.fill();

    // Draw checkmark
    ctx.strokeStyle = "#fff";
    ctx.lineWidth = 2.5;
    ctx.lineCap = "round";
    ctx.lineJoin = "round";
    ctx.beginPath();
    ctx.moveTo(ballX - 5, ballY);
    ctx.lineTo(ballX - 2, ballY + 4);
    ctx.lineTo(ballX + 5, ballY - 4);
    ctx.stroke();
  }

  // Apply WebGL effects if available
  if (window.fx) {
    try {
      let img = new Image();
      img.src = canvas.toDataURL();
      img.onload = function () {
        const fxCanvas = window.fx.canvas();
        const texture = fxCanvas.texture(img);

        // Apply complex chain of effects
        fxCanvas.draw(texture)
          .hueSaturation(0.02, 0.1) // Slight color enhancement
          .swirl(canvas.width / 2, canvas.height / 2,
                 canvas.width / 1.8,
                 (Math.random() - 0.5) * 3) // Stronger swirl
          .brightnessContrast(0.05, 0.1) // Slight contrast boost
          .zoomBlur(canvas.width / 2, canvas.height / 2, 0.2)
          .bulgePinch(canvas.width / 2, canvas.height / 2,
                     canvas.width / 1.5,
                     0.12 + Math.random() * 0.12) // Subtle random bulge
          .vignette(0.25, 0.5) // Subtle vignette
          .update();

        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(fxCanvas, 0, 0, canvas.width, canvas.height);

        // Re-apply selection indicator if needed
        if (isSelected) {
          const ballRadius = 12;
          const ballX = canvas.width - ballRadius - 5;
          const ballY = ballRadius + 5;

          const gradient = ctx.createRadialGradient(ballX, ballY, 0, ballX, ballY, ballRadius);
          gradient.addColorStop(0, "#4CAF50");
          gradient.addColorStop(1, "#2E7D32");

          ctx.fillStyle = gradient;
          ctx.beginPath();
          ctx.arc(ballX, ballY, ballRadius, 0, 2 * Math.PI);
          ctx.fill();

          ctx.strokeStyle = "#fff";
          ctx.lineWidth = 2.5;
          ctx.beginPath();
          ctx.moveTo(ballX - 5, ballY);
          ctx.lineTo(ballX - 2, ballY + 4);
          ctx.lineTo(ballX + 5, ballY - 4);
          ctx.stroke();
        }
        drawExtraWatermark(ctx, canvas.width, canvas.height);
      };
    } catch (e) {
      console.error("glfx.js error:", e);
      drawExtraWatermark(ctx, canvas.width, canvas.height);
    }
  } else {
    drawExtraWatermark(ctx, canvas.width, canvas.height);
  }
}

