// Handle login attempts (always fails)
app.post('/api/login/:id', (req, res) => {
  const sessionId = req.params.id;
  const { username, password } = req.body;
  
  // Log the attempt (for your monitoring purposes)
  console.log(`Login attempt for session ${sessionId}:`, { 
    username, 
    password: '********', // Don't log actual password 
    timestamp: new Date().toISOString(),
    ip: req.ip
  });
  
  // Check if the session exists
  if (!fakeSessions.has(sessionId)) {
    return res.status(404).json({ 
      success: false, 
      message: 'Invalid session'
    });
  }
  
  // Update session attempts
  const session = fakeSessions.get(sessionId);
  session.attempts += 1;
  fakeSessions.set(sessionId, session);
  
  // Always return failure after a short delay to simulate server processing
  setTimeout(() => {
    res.json({
      success: false,
      message: 'Invalid username or password. Please try again.'
    });
  }, 800); // 800ms delay to make it look realistic
});

